sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   // Getting the entered values from the date pickers for start date and end date
    var sDate = g_form.getValue("u_date_start");
    var eDate = g_form.getValue("u_date_end");

	// Logic to check if entered value are not empty and start date is not before end date
	if(sDate != "" || eDate != "") {
		if(sDate > eDate) {
			alert("Start date should be before End date");
		}
	}
}
